import UIKit

class MyProfileInteractor: MyProfileInteractorInputProtocol {

    weak var presenter: MyProfileInteractorOutputProtocol?
    
    func updateProfile(para: [String: Any]) {
        HUM.shared.editProfile(para: para) { (isSuccess, message) in
            if isSuccess {
                self.presenter?.successUpdate()
            } else {
                self.presenter?.failedUpdate(error: message)
            }
        }
    }
}
