import UIKit

class NavigationPresenter: NavigationPresenterProtocol, NavigationInteractorOutputProtocol {

    weak private var view: NavigationViewProtocol?
    var interactor: NavigationInteractorInputProtocol?
    private let router: NavigationWireframeProtocol

    init(interface: NavigationViewProtocol, interactor: NavigationInteractorInputProtocol?, router: NavigationWireframeProtocol) {
        self.view = interface
        self.interactor = interactor
        self.router = router
    }

    func addPressed() {
        router.addPressed()
    }
    func gotoInProgress() {
        router.gotoInProgress()
    }
    
}
